properties.property:
EVENTSTART
 Name:PageLoaded
 Source:footer

 SCRIPTSTART
 //
 // ===============================================
 // CONNECTOR FOR ALL TASKS v. 0.4
 // ===============================================
 //
 // Script binds ALL icons of the 'Smiley Face' used within
 // a lesson to their corresponding LimitedChecks. You need
 // to follow a proper naming convention and just update
 // `numberOfTasks` variable depending of number LimitedChecks
 // and Smiley Images you put on a page.
 //
 // 'Smileys' are named ImgSmiley-T#, where `#` stands for a
 // task number, and 'checks' just follow the same pattern:
 // `LimitedCheck-T#` i.e. 'ImgSmiley-T5' and 'LimitedCheck-T5'
 // means 'Smiley Image and Limited Check for the FIFTH task'.
 //

 // number of task available on page
 var numberOfTasks = 3,
 bindings = [];

 // bind Limited Check and it's Smiley for each task
 for(var i=1; i<=numberOfTasks; i++) {
 bindings.push(['LimitedCheck-T' + i, 'ImgSmiley-T' + i]);
 }

 // bind all required addons
 presenter.playerController.getHeaderModule('YC').bindLC(bindings);

 SCRIPTEND
EVENTEND



EVENTSTART
 Name:LimitedCheck
 Source:^LimitedCheck-T\:d+$

 SCRIPTSTART

 // Script matches ALL LimitedChecks that follow set naming
 // convention: `LimitedCheck-T#` where `#` stands for number
 // of a task i.e. 'LimitedCheck-T3' means "LimitedCheck for
 // the third task".
 //
 // When Event is trigged (user clicks given LimitedCheck)
 // proper Audio and Visual feedback is performed.
 // Depending on task result: an icon with the Smiley Face
 // is shown and `success` or `failure` sound will be played.

 var source = event.source ? event.source : '',
 match = source.match(/^LimitedCheck-T(\:d+)$/),
 taskMatch = match ? match[1] : '',
 smiley = presenter.playerController.getModule('ImgSmiley-T' + taskMatch),
 audioWrong = presenter.playerController.getModule('AudioWrong'),
 audioAward = presenter.playerController.getModule('AudioAward');

 // everything went well, the task was successfull
 if(event.score === event.maxScore && event.errors === '0') {
 audioWrong.stop();
 audioAward.stop();
 audioAward.play();
 smiley.show();

 // user response was faulty
 } else if(event.score <= event.maxScore) {
 audioWrong.stop();
 audioAward.stop();
 smiley.hide();

 if(event.errors !== '0') {
 audioWrong.play();
 }
 }
 SCRIPTEND
EVENTEND



EVENTSTART
 Source:^LimitedReset-T\:d+$

 SCRIPTSTART

 // Script matches ALL LimitedResets that follow set naming
 // convention: `LimitedReset-T#` where `#` stands for number
 // of a task i.e. 'LimitedReset-T3' means "LimitedReset for
 // the third task".
 //
 // When Event is trigged (user clicks given LimitedReset)
 // for a task - an icon with the Smiley Face will be hidden.
 //
 var source = event.source ? event.source : '',
 match = source.match(/^LimitedReset-T(\:d+)$/),
 taskMatch = match ? match[1] : '',
 smiley = presenter.playerController.getModule('ImgSmiley-T' + taskMatch);
 smiley.hide();

 SCRIPTEND
EVENTEND
